mapper.select:
select ah.*, s.supplier OrganName, p.Name HandsPersonName, u.username userName, a.Name AccountName
from jsh_account_head ah
left join jsh_supplier s on ah.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1'
left join jsh_user u on ah.creator=u.id and ifnull(u.Status,'0') ='0'
left join jsh_person p on ah.hands_person_id=p.id and ifnull(p.delete_Flag,'0') !='1'
left join jsh_account a on ah.account_id=a.id and ifnull(a.delete_Flag,'0') !='1'
where 1=1


    and ah.bill_no like #{bindBillNo}


    and ah.type=#{type}


    and ah.bill_time >= #{beginTime}


    and ah.bill_time <= #{endTime}


    and ah.organ_id=#{organId}


    and ah.hands_person_id=#{handsPersonId}


    and ah.creator=#{creator}


    and ah.creator in (

        #{creator}

    )

and ifnull(ah.delete_flag,'0') !='1'
order by ah.id desc

    limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_account_head
WHERE 1=1


    and bill_no like #{bindBillNo}


    and type=#{type}


    and bill_time >= #{beginTime}


    and bill_time <= #{endTime}


    and organ_id=#{organId}


    and hands_person_id=#{handsPersonId}


    and creator=#{creator}


    and creator in (

        #{creator}

    )

and ifnull(delete_flag,'0') !='1'

select sum(${modeName}) as allMoney from jsh_account_head
where type=#{type}
and organ_id =#{supplierId} and bill_time <= #{endTime}
and ifnull(delete_flag,'0') !='1'

select ah.*, s.supplier OrganName, p.Name HandsPersonName, a.Name AccountName from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jsh_person p on ah.hands_person_id=p.id and ifnull(p.delete_Flag,'0') !='1' left join jsh_account a on ah.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' where 1=1
and ah.bill_no = #{billNo}
and ifnull(ah.delete_flag,'0') !='1'

mapper.update:
update jsh_account_head
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

