mapper.select:
select ah.*, s.supplier OrganName, p.Name HandsPersonName, u.username userName, a.Name AccountName from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jsh_user u on ah.creator=u.id and ifnull(u.Status,'0') ='0' left join jsh_person p on ah.hands_person_id=p.id and ifnull(p.delete_Flag,'0') !='1' left join jsh_account a on ah.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' where 1=1
and ah.bill_no like '%${billNo}%'
and ah.type='${type}'
and ah.bill_time >= '${beginTime} 00:00:00'
and ah.bill_time <= '${endTime} 23:59:59'
and ah.creator in (
#{creator}
)
and ifnull(ah.delete_flag,'0') !='1' order by ah.id desc
limit #{offset},#{rows}

mapper.update:
update jsh_account_head
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_account_head where 1=1 and account_id in (
#{accountId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_account_head where 1=1 and organ_id in (
#{organId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_account_head where 1=1 and hands_person_id in (
#{handsPersonId}
) and ifnull(delete_flag,'0') !='1'
