mapper.select:
select *
FROM jsh_account
where 1=1


    and name like #{bindName}



    and serial_no like #{bindSerialNo}

and ifnull(delete_flag,'0') !='1'

select *
FROM jsh_account
where 1=1


    and name like #{bindName}



    and serial_no like #{bindSerialNo}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'
order by sort asc, id desc

    limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_account
WHERE 1=1


    and name like #{bindName}



    and serial_no like #{bindSerialNo}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

select dh.number,concat(dh.sub_type,dh.type) as newType, 'bill' as fromType, s.supplier,dh.change_amount,date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from jsh_depot_head dh left join jsh_supplier s on dh.organ_id = s.id and ifnull(s.delete_flag,'0') !='1' where 1=1
and dh.account_id=#{accountId}
and ifnull(dh.delete_flag,'0') !='1'
UNION ALL select ah.bill_no,ah.type as newType, 'financial' as fromType, s.supplier,ah.change_amount,date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id and ifnull(s.delete_flag,'0') !='1' where ah.type!='转账'
and ah.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select ah.bill_no,ah.type as newType, 'financial' as fromType, s.supplier,ai.each_amount,date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id inner join jsh_account_item ai on ai.header_id=ah.id and ifnull(ai.delete_flag,'0') !='1' where ah.type in ('收款','付款','收预付款')
and ai.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select ah.bill_no,ah.type as newType, 'financial' as fromType, '' as sName,ah.change_amount,date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from jsh_account_head ah inner join jsh_account_item ai on ai.header_id=ah.id and ifnull(ai.delete_flag,'0') !='1' where ah.type='转账'
and ah.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select ah.bill_no,ah.type as newType, 'financial' as fromType, '' as sName,ai.each_amount,date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from jsh_account_head ah inner join jsh_account_item ai on ai.header_id=ah.id and ifnull(ai.delete_flag,'0') !='1' where ah.type='转账'
and ai.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select dh.number,concat(dh.sub_type,dh.type) as newType, 'bill' as fromType, s.supplier,dh.change_amount,date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime, dh.account_id_list as AList,dh.account_money_list as AMList from jsh_depot_head dh left join jsh_supplier s on dh.organ_id = s.id and ifnull(s.delete_flag,'0') !='1' where 1=1
and dh.account_id_list like #{bindAccountId}
and ifnull(dh.delete_flag,'0') !='1' ORDER BY oTime desc
limit #{offset},#{rows}

select sum(a) from (
select count(1) a from jsh_depot_head dh left join jsh_supplier s on dh.organ_id = s.id and ifnull(s.delete_flag,'0') !='1' where 1=1
and dh.account_id=#{accountId}
and ifnull(dh.delete_flag,'0') !='1'
UNION ALL select count(1) a from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id and ifnull(s.delete_flag,'0') !='1' where ah.type!='转账'
and ah.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select count(1) a from jsh_account_head ah left join jsh_supplier s on ah.organ_id=s.id inner join jsh_account_item ai on ai.header_id=ah.Id and ifnull(ai.delete_flag,'0') !='1' where ah.Type in ('收款','付款','收预付款')
and ai.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select count(1) a from jsh_account_head ah inner join jsh_account_item ai on ai.header_id=ah.id and ifnull(ai.delete_flag,'0') !='1' where ah.type='转账'
and ah.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select count(1) a from jsh_account_head ah inner join jsh_account_item ai on ai.header_id=ah.id and ifnull(ai.delete_flag,'0') !='1' where ah.type='转账'
and ai.account_id=#{accountId}
and ifnull(ah.delete_flag,'0') !='1'
UNION ALL select count(1) a from jsh_depot_head dh left join jsh_supplier s on dh.organ_id = s.id and ifnull(s.delete_flag,'0') !='1' where 1=1
and dh.account_id_list like #{bindAccountId}
and ifnull(dh.delete_flag,'0') !='1' ) cc

