mapper.select:
select distinct dh.*, s.supplier OrganName, u.username userName, a.name AccountName
from jsh_depot_head dh
left join jsh_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1'
left join jsh_user u on dh.creator=u.id and ifnull(u.Status,'0') ='0'
left join jsh_account a on dh.account_id=a.id and ifnull(a.delete_Flag,'0') !='1'
left join jsh_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag,'0') !='1'
left join jsh_material m on di.material_id = m.id and ifnull(m.delete_flag,'0') !='1'
left join jsh_material_extend me on di.material_extend_id = me.id and ifnull(me.delete_flag,'0') !='1'
where 1=1

    and dh.type=#{type}


    and dh.sub_type=#{subType}


    and dh.status in (

        #{status}

    )



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and dh.organ_id=#{organId}


    and dh.creator=#{creator}


    and di.depot_id=#{depotId}


    and di.depot_id in (

        #{depotId}

    )


    and dh.creator in (

        #{creator}

    )

and ifnull(dh.delete_flag,'0') !='1'
order by dh.id desc

    limit #{offset},#{rows}

SELECT
COUNT(1) from
(select distinct dh.* FROM jsh_depot_head dh
left join jsh_depot_item di on dh.Id = di.header_id and ifnull(di.delete_flag,'0') !='1'
left join jsh_material m on di.material_id = m.Id and ifnull(m.delete_Flag,'0') !='1'
left join jsh_material_extend me on di.material_extend_id = me.id and ifnull(me.delete_flag,'0') !='1'
WHERE 1=1

    and dh.type=#{type}


    and dh.sub_type=#{subType}


    and dh.status in (

        #{status}

    )



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and dh.organ_id=#{organId}


    and dh.creator=#{creator}


    and di.depot_id=#{depotId}


    and di.depot_id in (

        #{depotId}

    )


    and dh.creator in (

        #{creator}

    )

and ifnull(dh.delete_Flag,'0') !='1') tb

select dh.number,me.bar_code, m.`name` MName,m.model,m.standard,di.unit_price,di.material_unit as mUnit,di.oper_number,di.all_price,s.supplier SName,d.dName DName, date_format(dh.oper_time, '%Y-%m-%d') OperTime, concat(dh.sub_type,dh.type) as NewType, concat_ws(' ',dh.remark,di.remark) as newRemark from jsh_depot_head dh left join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1' left join jsh_material m on m.id=di.material_id and ifnull(m.delete_flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' left join (select id,name as dName,delete_Flag from jsh_depot ) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1' where dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}
and dh.organ_id = #{oId}
and di.depot_id = #{depotId}
and dh.type=#{type}
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and dh.sub_type!='调拨' and ifnull(dh.delete_flag,'0') !='1' ORDER BY oper_time DESC,number desc
limit #{offset},#{rows}

select count(1) from jsh_depot_head dh left join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1' left join jsh_material m on m.id=di.material_id and ifnull(m.delete_Flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1' left join (select id,name as dName,delete_Flag from jsh_depot) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1' where dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}
and dh.organ_id = #{oId}
and di.depot_id = #{depotId}
and dh.type=#{type}
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and dh.sub_type!='调拨' and ifnull(dh.delete_flag,'0') !='1' ORDER BY oper_time DESC,number desc

select di.material_id, m.bar_code, m.mName,m.Model,m.standard,m.categoryName,m.materialUnit, sum(di.basic_number) numSum, sum(di.all_price) priceSum from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' left join (select jsh_material.id,jsh_material.name mName, me.bar_code, Model, standard, jsh_material_category.`Name` categoryName, concat_ws('', jsh_material.unit, u.basic_unit) materialUnit from jsh_material left join jsh_unit u on jsh_material.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' left join jsh_material_extend me on me.material_id=jsh_material.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_category on jsh_material.category_id=jsh_material_category.Id and ifnull(jsh_material_category.delete_flag,'0') !='1' where me.default_flag=1 and ifnull(jsh_material.delete_Flag,'0') !='1' ) m on m.Id=di.material_id where (dh.type=#{type} or dh.sub_type='调拨') and dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}
and dh.organ_id = #{oId}
and ((di.depot_id = #{depotId} and dh.sub_type!='调拨') or (di.another_depot_id = #{depotId} and dh.sub_type='调拨'))
and di.depot_id = #{depotId}
and (m.bar_code like #{bindKey} or m.mName like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and ifnull(dh.delete_flag,'0') !='1' group by di.material_id
limit #{offset},#{rows}

select count(1) from (select di.material_id, m.mName,m.bar_code,m.Model,m.standard,m.categoryName from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' left join (select jsh_material.id,jsh_material.name mName, me.bar_code, Model,standard,jsh_material_category.`Name` categoryName from jsh_material left join jsh_material_extend me on me.material_id=jsh_material.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_category on jsh_material.category_id=jsh_material_category.Id and ifnull(jsh_material_category.delete_flag,'0') !='1' where me.default_flag=1 and ifnull(jsh_material.delete_Flag,'0') !='1' ) m on m.Id=di.material_id where (dh.type=#{type} or dh.sub_type='调拨') and dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}
and dh.organ_id = #{oId}
and ((di.depot_id = #{depotId} and dh.sub_type!='调拨') or (di.another_depot_id = #{depotId} and dh.sub_type='调拨'))
and di.depot_id = #{depotId}
and (m.bar_code like #{bindKey} or m.mName like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and ifnull(dh.delete_flag,'0') !='1' group by di.material_id) a

select dh.number,me.bar_code, m.`name` MName,m.model,m.standard,di.unit_price,di.material_unit as mUnit,di.oper_number,di.all_price,SName, d.dName DName,
date_format(dh.oper_time, '%Y-%m-%d') OperTime, concat(dh.sub_type,dh.type) as NewType, concat_ws('  ',dh.remark,di.remark) as newRemark
from jsh_depot_head dh
left join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1'
left join jsh_material m on m.id=di.material_id and ifnull(m.delete_flag,'0') !='1'
left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1'
left join (select id,name as dName,delete_Flag from jsh_depot ) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1'

left join (select id as aid,name as SName,delete_Flag as adelete_Flag from jsh_depot ) ddd on ddd.aid=di.another_depot_id and ifnull(ddd.adelete_Flag,'0') !='1'
where dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

    and di.depot_id = #{depotIdF}


    and di.another_depot_id = #{depotId}


    and dh.sub_type=#{subType}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey} or dh.remark like #{bindKey})

and ifnull(dh.delete_flag,'0') !='1'
ORDER BY oper_time DESC,number desc

    limit #{offset},#{rows}

select count(1)
from jsh_depot_head dh
left join jsh_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1'
left join jsh_material m on m.id=di.material_id and ifnull(m.delete_Flag,'0') !='1'
left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1'
left join (select id,name as dName,delete_Flag from jsh_depot) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1'
where dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

    and di.depot_id = #{depotIdF}


    and di.another_depot_id = #{depotId}


    and dh.sub_type=#{subType}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey} or dh.remark like #{bindKey})

and ifnull(dh.delete_flag,'0') !='1'
ORDER BY oper_time DESC,number desc

select dh.number,concat(dh.sub_type,dh.type) as type,dh.discount_last_money,dh.other_money,dh.change_amount,s.supplier supplierName,
date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_depot_head dh
left join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1'
where s.type=#{supType} and (dh.sub_type!='其它' and dh.sub_type!='采购订单' and dh.sub_type!='销售订单')
and dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

    and dh.organ_id=#{organId}

and ifnull(dh.delete_flag,'0') !='1'
UNION ALL
select ah.bill_no number,ah.type as newType,ah.total_price discount_last_money, 0 other_money,ah.change_amount,s.supplier supplierName,
date_format(ah.bill_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_account_head ah
left join jsh_supplier s on s.id=ah.organ_id and ifnull(s.delete_Flag,'0') !='1'
where s.type=#{supType}
and ah.bill_time >=#{beginTime} and ah.bill_time <=#{endTime}

    and ah.organ_id=#{organId}

and ifnull(ah.delete_flag,'0') !='1'
ORDER BY oTime

    limit #{offset},#{rows}

select sum(a) from
(
    select count(1) a from jsh_depot_head dh
    left join jsh_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1'
    where s.type=#{supType} and (dh.sub_type!='其它' and dh.sub_type!='采购订单' and dh.sub_type!='销售订单')
    and dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

        and dh.organ_id=#{organId}

    and ifnull(dh.delete_flag,'0') !='1'
    UNION ALL
    select count(1) a from jsh_account_head ah
    left join jsh_supplier s on s.id=ah.organ_id and ifnull(s.delete_Flag,'0') !='1'
    where s.type=#{supType}
    and ah.bill_time >=#{beginTime} and ah.bill_time <=#{endTime}

        and ah.organ_id=#{organId}

    and ifnull(ah.delete_flag,'0') !='1'
) cc

select ifnull(sum(${modeName}),0) as allMoney from jsh_depot_head where type=#{type} and sub_type = #{subType}
and organ_id =#{supplierId} and oper_time <=#{endTime}
and ifnull(delete_flag,'0') !='1'

select ifnull(sum(other_money),0) as allOtherMoney from jsh_depot_head where type=#{type} and sub_type = #{subType}
and organ_id =#{supplierId} and oper_time <=#{endTime}
and ifnull(delete_flag,'0') !='1'

select dh.*, concat(dh.sub_type,dh.type) as depotHeadType, s.supplier OrganName, a.name AccountName from jsh_depot_head dh left join jsh_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jsh_account a on dh.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' where 1=1
and dh.number=#{number}
and ifnull(dh.delete_flag,'0') !='1'

mapper.update:
update jsh_depot_head
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_depot_head where 1=1 and account_id in (
#{accountId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_head where 1=1 and organ_id in (
#{organId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_head where 1=1 and creator in (
#{creator}
) and ifnull(delete_flag,'0') !='1'

SELECT
ifnull(sum(discount_last_money),0)
FROM jsh_depot_head
WHERE 1=1

    and type=#{type}


    and sub_type=#{subType}


    and organ_id is not null


    and organ_id is null


    and oper_time >= #{beginTime}


    and oper_time <= #{endTime}

and ifnull(delete_flag,'0') !='1'

SELECT
ifnull(sum(total_price),0)
FROM jsh_depot_head
WHERE 1=1

    and type=#{type}


    and sub_type=#{subType}


    and organ_id is not null


    and organ_id is null


    and oper_time >= #{beginTime}


    and oper_time <= #{endTime}

and ifnull(delete_flag,'0') !='1'

select distinct dh.*, s.supplier OrganName, u.username userName
from jsh_depot_head dh
left join jsh_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1'
left join jsh_user u on dh.creator=u.id and ifnull(u.Status,'0') ='0'
left join jsh_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag,'0') !='1'
left join jsh_material m on di.material_id = m.id and ifnull(m.delete_flag,'0') !='1'
where 1=1

    and dh.organ_id = #{organId}


    and dh.type=#{type}


    and dh.sub_type=#{subType}


    and dh.status =#{status}



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}



    and (m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and di.depot_id in (

        #{depotId}

    )


    and dh.creator in (

        #{creator}

    )

and (abs(dh.change_amount)+(select ifnull(sum(abs(ai.each_amount)),0) from jsh_account_item ai where ai.bill_id=dh.id and ifnull(ai.delete_flag,'0') !='1'))
< (dh.discount_last_money + dh.other_money)
and ifnull(dh.delete_flag,'0') !='1'
order by dh.id desc
