mapper.select:
select *
FROM jsh_depot_item
where 1=1


    and name like #{bindName}


    and type = #{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

    limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_depot_item
WHERE 1=1


    and name like #{bindName}


    and type = #{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

select tb.* from (select dh.number,me.bar_code,m.name material_name,dh.type,dh.sub_type, case when type='入库' then ifnull(di.basic_number,0) when type='出库' then 0-di.basic_number when dh.sub_type='组装单' and di.material_type='组合件' then ifnull(di.basic_number,0) when dh.sub_type='组装单' and di.material_type='普通子件' then 0-di.basic_number when dh.sub_type='拆卸单' and di.material_type='普通子件' then ifnull(di.basic_number,0) when dh.sub_type='拆卸单' and di.material_type='组合件' then 0-di.basic_number when dh.sub_type='盘点复盘' then ifnull(di.basic_number,0) else 0 end as b_num, (select name from jsh_depot d where d.id=di.depot_id and ifnull(d.delete_flag,'0') !='1') as depotName, date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' left join jsh_material m on di.material_id=m.id and ifnull(m.delete_flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' where (dh.type!='其它' or (dh.type='其它' and dh.sub_type='组装单') or (dh.type='其它' and dh.sub_type='拆卸单') or (dh.type='其它' and dh.sub_type='盘点复盘'))
and di.depot_id in ( #{depotId} )
and dh.status = '1'
and di.sku = #{sku}
and di.batch_number = #{batchNumber}
and dh.number like #{bindNumber}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
and di.material_id = #{mId} and ifnull(dh.delete_flag,'0') !='1' union all --单独构造记录：调拨入库 select dh.number,me.bar_code,m.name material_name,dh.type,dh.sub_type, ifnull(di.basic_number,0) as b_num, (select concat(name,'[调入]') from jsh_depot d where d.id=di.another_depot_id and ifnull(d.delete_flag,'0') !='1') as depotName, date_format(dh.oper_time,'%Y-%m-%d %H:%i:%S') as oTime from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' left join jsh_material m on di.material_id=m.id and ifnull(m.delete_flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' where dh.type='出库' and dh.sub_type='调拨'
and di.another_depot_id in ( #{depotId} )
and dh.status = '1'
and di.sku = #{sku}
and di.batch_number = #{batchNumber}
and dh.number like #{bindNumber}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
and di.material_id = #{mId} and ifnull(dh.delete_flag,'0') !='1') tb order by tb.oTime desc
limit #{offset},#{rows}

select count(tb.number) from
(select dh.number from jsh_depot_head dh
left JOIN jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1'
where (dh.type!='其它'
or (dh.type='其它' and dh.sub_type='组装单')
or (dh.type='其它' and dh.sub_type='拆卸单')
or (dh.type='其它' and dh.sub_type='盘点复盘'))

    and di.depot_id in (
    #{depotId}
    )


    and dh.status = '1'


    and di.sku = #{sku}


    and di.batch_number = #{batchNumber}



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and di.material_id =#{mId}
and ifnull(dh.delete_flag,'0') !='1'
union all
--单独构造记录：调拨入库
select dh.number from jsh_depot_head dh
left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1'
where dh.type='出库' and dh.sub_type='调拨'

    and di.another_depot_id in (
    #{depotId}
    )


    and dh.status = '1'


    and di.sku = #{sku}


    and di.batch_number = #{batchNumber}



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and di.material_id = #{mId}
and ifnull(dh.delete_flag,'0') !='1') tb

select m.id MId, me.bar_code, m.name MName, m.mfrs MMfrs, m.model MModel, m.standard MStandard, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3, concat_ws('', m.unit, u.basic_unit) MaterialUnit, m.color MColor, m.unit_id, u.name unit_name, ifnull(me.purchase_decimal,0) purchase_decimal from jsh_material m left join jsh_material_extend me on me.material_id=m.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_depot_item di on di.material_id=m.id and ifnull(di.delete_Flag,'0') !='1' left join jsh_depot_head dh on di.header_id=dh.id and ifnull(dh.delete_flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where 1=1 and me.default_flag=1
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and dh.oper_time <= #{endTime}
and ifnull(m.delete_flag,'0') !='1' group by m.id order by m.id desc
limit #{offset},#{rows}

select m.id MId, me.bar_code, m.name MName, m.mfrs MMfrs, m.model MModel, m.standard MStandard, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3, concat_ws('', m.unit, u.basic_unit) MaterialUnit, m.color MColor, u.name unit_name from jsh_material m left join jsh_depot_item di on di.material_id=m.id and ifnull(di.delete_Flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_depot_head dh on di.header_id=dh.id and ifnull(dh.delete_flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where 1=1
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and (dh.sub_type = '采购' or dh.sub_type = '采购退货')
and (dh.sub_type = '销售' or dh.sub_type = '销售退货' or dh.sub_type = '零售' or dh.sub_type = '零售退货')
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
