select sum(case when dh.type='入库' then di.basic_number else 0 end) as inTotal, sum(case when dh.type='出库' and dh.sub_type!='调拨' then di.basic_number else 0 end) as outTotal, sum(case when dh.sub_type='调拨' then di.basic_number else 0 end) as transfInTotal, sum(case when dh.sub_type='调拨' then di.basic_number else 0 end) as transfOutTotal, sum(case when dh.sub_type='组装单' and di.material_type='组合件' then di.basic_number else 0 end) as assemInTotal, sum(case when dh.sub_type='组装单' and di.material_type='普通子件' then di.basic_number else 0 end) as assemOutTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='普通子件' then di.basic_number else 0 end) as disAssemInTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='组合件' then di.basic_number else 0 end) as disAssemOutTotal from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' where 1=1 and ifnull(dh.delete_flag,'0') !='1' and di.material_extend_id=#{meId} and ifnull(di.sku,'') !=''
and dh.status = '1'
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}

select sum(case when dh.type='入库' then di.basic_number else 0 end) as inTotal, sum(case when dh.type='出库' and dh.sub_type!='调拨' then di.basic_number else 0 end) as outTotal, sum(case when dh.sub_type='调拨' then di.basic_number else 0 end) as transfInTotal, sum(case when dh.sub_type='调拨' then di.basic_number else 0 end) as transfOutTotal, sum(case when dh.sub_type='组装单' and di.material_type='组合件' then di.basic_number else 0 end) as assemInTotal, sum(case when dh.sub_type='组装单' and di.material_type='普通子件' then di.basic_number else 0 end) as assemOutTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='普通子件' then di.basic_number else 0 end) as disAssemInTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='组合件' then di.basic_number else 0 end) as disAssemOutTotal from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' where 1=1 and ifnull(dh.delete_flag,'0') !='1' and di.material_id=#{mId}
and dh.status = '1'
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}

select dep.id,dep.header_id,dep.material_id,dep.material_unit,dep.oper_number, dep.basic_number,dep.unit_price,dep.tax_unit_price,dep.all_price,dep.remark, dep.depot_id,dep.another_depot_id,dep.tax_rate, dep.tax_money,dep.tax_last_money,dep.material_type from jsh_depot_item dep,jsh_material mat where 1=1
and dep.header_id = #{depotheadId}
and dep.material_id=mat.id
and mat.enable_serial_number = #{enableSerialNumber}
and ifnull(dep.delete_flag,'0') !='1' and ifnull(mat.delete_flag,'0') !='1'

