mapper.select:
select
from jsh_depot_item where 1=1 and depot_id in (
#{depotId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_item where 1=1 and material_id in (
#{materialId}
) and ifnull(delete_flag,'0') !='1'

select m.id MId, m.name MName, me.bar_code, m.mfrs MMfrs, m.model MModel, m.standard MStandard,m.color MColor, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3,d.name depotName, m.unit MaterialUnit, u.basic_unit unit_name,mcs.current_number, mis.low_safe_stock, mis.high_safe_stock from jsh_material m left join jsh_material_extend me on me.material_id=m.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_initial_stock mis on mis.material_id=m.id and ifnull(mis.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on mcs.material_id=m.id and ifnull(mcs.delete_Flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' left join jsh_depot d on d.id=mis.depot_id and ifnull(u.delete_flag,'0') !='1' where 1=1 and me.default_flag=1 and ifnull(m.delete_flag,'0') !='1' and mis.depot_id=mcs.depot_id and ((ifnull(mis.low_safe_stock,0)!=0 and mcs.current_number < ifnull(mis.low_safe_stock,0)) or (ifnull(mis.high_safe_stock,0)!=0 and mcs.current_number > ifnull(mis.high_safe_stock,0)))
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and mis.depot_id in
#{item}
order by depotName asc
limit #{offset},#{rows}

select count(1) from (select m.id from jsh_material m left join jsh_material_extend me on me.material_id=m.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_initial_stock mis on mis.material_id=m.id and ifnull(mis.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on mcs.material_id=m.id and ifnull(mcs.delete_Flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' left join jsh_depot d on d.id=mis.depot_id and ifnull(u.delete_flag,'0') !='1' where 1=1 and me.default_flag=1 and ifnull(m.delete_flag,'0') !='1' and mis.depot_id=mcs.depot_id and ((ifnull(mis.low_safe_stock,0)!=0 and mcs.current_number < ifnull(mis.low_safe_stock,0)) or (ifnull(mis.low_safe_stock,0)!=0 and mcs.current_number > ifnull(mis.high_safe_stock,0)))
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and mis.depot_id in
#{item}
) tb

