select ifnull(sum(di.basic_number),0) from jsh_depot_item di
where di.material_extend_id=#{meId}
and di.link_id=#{linkId}
and ifnull(di.delete_flag,'0') !='1'
and di.header_id
in
(
select dh.id from jsh_depot_head dh
where
dh.link_number=#{linkNumber}
and dh.id!=#{currentHeaderId}
and ifnull(dh.delete_flag,'0') !='1'

    and dh.sub_type=#{goToType}

)

select id, bar_code, name, standard, model, unit_id, commodity_unit, batch_number, expiration_date, sum(basic_number) total_num from (select di.batch_number id, me.bar_code, m.name, m.standard, m.model, m.unit_id, me.commodity_unit, di.batch_number,di.expiration_date, di.basic_number*(case dh.type when '入库' then 1 when '出库' then -1 end) as basic_number from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id left join jsh_material m on m.id=di.material_id and ifnull(m.delete_flag, '0') != '1' left join jsh_material_extend me on me.material_id=m.id and ifnull(me.delete_flag,'0') !='1' where me.bar_code= #{barCode}
and di.batch_number like #{bindName}
and di.depot_id= #{depotId}
and di.batch_number= #{batchNumber}
and dh.number!= #{number}
and m.enable_batch_number =1 and di.delete_flag!=1) tb group by batch_number order by expiration_date asc

select count(1) from jsh_depot_item di
where di.material_id=#{mId} and di.depot_id=#{depotId}
and ifnull(di.delete_flag,'0') !='1'
