select m.*,me.bar_code,u.name unitName, mc.name categoryName FROM jsh_material m left join jsh_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left JOIN jsh_unit u on m.unit_id = u.id and ifnull(u.delete_flag,'0') !='1' left JOIN jsh_material_category mc on m.category_id = mc.id and ifnull(mc.delete_Flag,'0') !='1' where 1=1
and me.id = #{meId}
and ifnull(m.delete_flag,'0') !='1'

select m.*,u.name unit_name, me.id meId,me.bar_code m_bar_code, me.commodity_unit, me.purchase_decimal, me.commodity_decimal, me.wholesale_decimal, me.low_decimal, me.sku from jsh_material m left join jsh_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where me.bar_code in (
#{barCode}
) and ifnull(m.delete_flag,'0') !='1' order by m.id desc, me.default_flag desc, me.id asc

select m.*,u.name unit_name, me.id meId,me.bar_code m_bar_code, me.commodity_unit, me.purchase_decimal, me.commodity_decimal, me.wholesale_decimal, me.low_decimal, me.sku from jsh_material m left join jsh_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where me.bar_code in (
#{barCode}
) and me.material_id!=#{mId} and ifnull(m.delete_flag,'0') !='1' order by me.id desc

select m.*, me.commodity_unit unitName, mc.name categoryName, me.bar_code, ifnull(me.purchase_decimal,0) purchase_decimal, ifnull(sum(mcs.current_number),0) currentStock, sum(ifnull(me.purchase_decimal, 0) * ifnull(mcs.current_number, 0)) currentStockPrice, sum(ifnull(m.weight, 0) * ifnull(mcs.current_number, 0)) currentWeight FROM jsh_material m left JOIN jsh_material_extend me on m.id = me.material_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on m.id = mcs.material_id and ifnull(mcs.delete_flag,'0') !='1' left JOIN jsh_unit u on m.unit_id = u.id and ifnull(u.delete_Flag,'0') !='1' left JOIN jsh_material_category mc on m.category_id = mc.id and ifnull(mc.delete_Flag,'0') !='1' where 1=1 and me.default_flag=1
and mcs.depot_id in
#{item}
and m.category_id in
#{item}
and m.position like #{bindPosition}
and (me.bar_code like #{bindParam} or m.name like #{bindParam} or m.standard like #{bindParam} or m.model like #{bindParam} or m.color like #{bindParam})
and ifnull(m.delete_flag,'0') !='1' group by m.id
having currentStock!=0
order by m.id desc
order by ${column} ${order}
limit #{offset},#{rows}

select count(tb.id) from (select m.id, ifnull(sum(mcs.current_number),0) currentStock from jsh_material m left JOIN jsh_material_extend me on m.id = me.material_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on m.id = mcs.material_id and ifnull(mcs.delete_flag,'0') !='1' left JOIN jsh_unit u on m.unit_id = u.id and ifnull(u.delete_Flag,'0') !='1' left JOIN jsh_material_category mc on m.category_id = mc.id and ifnull(mc.delete_Flag,'0') !='1' where 1=1 and me.default_flag=1
and mcs.depot_id in
#{item}
and m.category_id in
#{item}
and m.position like #{bindPosition}
and (me.bar_code like #{bindParam} or m.name like #{bindParam} or m.standard like #{bindParam} or m.model like #{bindParam} or m.color like #{bindParam})
and ifnull(m.delete_flag,'0') !='1' group by m.id
having currentStock!=0
) tb

select ifnull(sum(mcs.current_number),0) currentStock, sum(ifnull(me.purchase_decimal,0)*ifnull(mcs.current_number,0)) currentStockPrice, sum(ifnull(m.weight,0)*ifnull(mcs.current_number,0)) currentWeight from jsh_material m left JOIN jsh_material_extend me on m.id = me.material_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on m.id = mcs.material_id and ifnull(mcs.delete_flag,'0') !='1' left JOIN jsh_unit u on m.unit_id = u.id and ifnull(u.delete_Flag,'0') !='1' left JOIN jsh_material_category mc on m.category_id = mc.id and ifnull(mc.delete_Flag,'0') !='1' where 1=1 and me.default_flag=1
and mcs.depot_id in
#{item}
and m.category_id in
#{item}
and m.position like #{bindPosition}
and (me.bar_code like #{bindParam} or m.name like #{bindParam} or m.standard like #{bindParam} or m.model like #{bindParam})
and ifnull(m.delete_flag,'0') !='1'

select count(1) from jsh_material m
where ifnull(m.delete_flag,'0') !='1'

    and m.name = #{name}


    and m.model = #{model}


    and m.color = #{color}


    and m.standard = #{standard}


    and m.mfrs = #{mfrs}


    and m.other_field1 = #{otherField1}


    and m.other_field2 = #{otherField2}


    and m.other_field3 = #{otherField3}


    and m.unit = #{unit}


    and m.unit_id = #{unitId}


    and m.id != #{id}
