mapper.select:
SELECT ,#{currentId} as currentId FROM jsh_organization WHERE parent_id = #{id}
and id !=#{currentId}
and ifnull(delete_flag,'0') !='1' order by sort asc

SELECT ,#{currentId} as currentId FROM jsh_organization WHERE parent_id is null
and id !=#{currentId}
and ifnull(delete_flag,'0') !='1' order by sort asc

select * from jsh_organization where 1=1 and ifnull(delete_flag,'0') !='1' and parent_id in (
#{id}
)

mapper.update:
update jsh_organization
set update_Time=#{updateTime},delete_flag='1'
where id in (

    #{id}

)

update jsh_organization
set update_time=#{updateTime},
org_no = #{orgNo},org_abr = #{orgAbr},
delete_flag = #{deleteFlag},parent_id = #{parentId},
sort = #{sort},remark = #{remark}
where id =#{id}

mapper.select:
select * from jsh_organization where 1=1 and ifnull(delete_flag,'0') !='1' and parent_id is null and org_abr='根机构' and id in (
#{id}
