mapper.select:
select dh.Number,m.`name` MName,m.Model,di.UnitPrice,di.OperNumber,di.AllPrice,s.supplier SName,d.dName DName, date_format(dh.OperTime, '%Y-%m-%d') OperTime, concat(dh.SubType,dh.Type) as NewType from jsh_depothead dh inner join jsh_depotitem di on di.HeaderId=dh.id inner join jsh_material m on m.id=di.MaterialId inner join jsh_supplier s on s.id=dh.OrganId inner join (select id,name as dName from jsh_depot) d on d.id=di.DepotId where dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'
and dh.OrganId = ${oId}
and di.DepotId = ${pid}
and di.DepotId in (${dids})
and dh.Type='${type}'
ORDER BY OperTime DESC,Number desc
limit #{offset},#{rows}

select count(1) from jsh_depothead dh inner join jsh_depotitem di on di.HeaderId=dh.id inner join jsh_material m on m.id=di.MaterialId inner join jsh_supplier s on s.id=dh.OrganId inner join (select id,name as dName from jsh_depot) d on d.id=di.DepotId where dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'
and dh.OrganId = ${oId}
and di.DepotId = ${pid}
and di.DepotId in (${dids})
and dh.Type='${type}'
ORDER BY OperTime DESC,Number desc

select di.MaterialId, m.mName,m.Model,m.categoryName,

(select sum(jdi.BasicNumber) numSum from jsh_depothead jdh INNER JOIN jsh_depotitem jdi
on jdh.id=jdi.HeaderId where jdi.MaterialId=di.MaterialId
and jdh.type='${type}' and jdh.OperTime >='${beginTime}' and jdh.OperTime <='${endTime}'

    and jdh.OrganId = ${oId}


    and jdi.DepotId= ${pid}


    and jdi.DepotId in (${dids})

) numSum,

(select sum(jdi.AllPrice) priceSum from jsh_depothead jdh INNER JOIN jsh_depotitem jdi
on jdh.id=jdi.HeaderId where jdi.MaterialId=di.MaterialId
and jdh.type='${type}' and jdh.OperTime >='${beginTime}' and jdh.OperTime <='${endTime}'

    and jdh.OrganId = ${oId}


    and jdi.DepotId= ${pid}


    and jdi.DepotId in (${dids})

) priceSum
from jsh_depothead dh INNER JOIN jsh_depotitem di on dh.id=di.HeaderId
INNER JOIN (SELECT jsh_material.id,jsh_material.name mName, Model,jsh_materialcategory.`Name` categoryName
from jsh_material INNER JOIN jsh_materialcategory on jsh_material.CategoryId=jsh_materialcategory.Id) m
on m.Id=di.MaterialId where dh.type='${type}' and dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'

    and dh.OrganId = ${oId}


    and di.DepotId= ${pid}


    and di.DepotId in (${dids})

GROUP BY di.MaterialId,m.mName,m.Model,m.categoryName

    limit #{offset},#{rows}

select count(1) from (select di.MaterialId, m.mName,m.Model,m.categoryName from jsh_depothead dh INNER JOIN jsh_depotitem di on dh.id=di.HeaderId INNER JOIN (SELECT jsh_material.id,jsh_material.name mName, Model,jsh_materialcategory.`Name` categoryName from jsh_material INNER JOIN jsh_materialcategory on jsh_material.CategoryId=jsh_materialcategory.Id) m on m.Id=di.MaterialId where dh.type='${type}' and dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'
and dh.OrganId = ${oId}
and di.DepotId= ${pid}
and di.DepotId in (${dids})
GROUP BY di.MaterialId,m.mName,m.Model,m.categoryName) a

select dh.Number,concat(dh.SubType,dh.Type) as type,dh.DiscountLastMoney,dh.ChangeAmount,s.supplier supplierName, date_format(dh.OperTime,'%Y-%m-%d %H:%i:%S') as oTime from jsh_depothead dh inner join jsh_supplier s on s.id=dh.OrganId where s.type='${supType}' and (dh.SubType!='其它' and dh.SubType!='采购订单' and dh.SubType!='销售订单') and dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'
and dh.OrganId=${organId}
UNION ALL select ah.BillNo Number,ah.Type as newType,ah.TotalPrice DiscountLastMoney,ah.ChangeAmount,s.supplier supplierName, date_format(ah.BillTime,'%Y-%m-%d %H:%i:%S') as oTime from jsh_accounthead ah inner join jsh_supplier s on s.id=ah.OrganId where s.type='${supType}' and ah.BillTime >='${beginTime}' and ah.BillTime <='${endTime}'
and ah.OrganId=${organId}
ORDER BY oTime
limit #{offset},#{rows}

select sum(a) from ( select count(1) a from jsh_depothead dh inner join jsh_supplier s on s.id=dh.OrganId where s.type='${supType}' and (dh.SubType!='其它' and dh.SubType!='采购订单' and dh.SubType!='销售订单') and dh.OperTime >='${beginTime}' and dh.OperTime <='${endTime}'
and dh.OrganId=${organId}
UNION ALL select count(1) a from jsh_accounthead ah inner join jsh_supplier s on s.id=ah.OrganId where s.type='${supType}' and ah.BillTime >='${beginTime}' and ah.BillTime <='${endTime}'
and ah.OrganId=${organId}
) cc
