mapper.select:
select * FROM jsh_depot_item where 1=1
and name like '%${name}%'
and type=${type}
and remark like '%${remark}%'
and ifnull(delete_flag,'0') !='1'
limit #{offset},#{rows}

select dep.id,dep.header_id,dep.material_id,dep.materialUnit,dep.oper_number, dep.basic_number,dep.unit_price,dep.tax_unit_price,dep.all_price,dep.remark, dep.img,dep.incidentals,dep.depot_id,dep.another_depot_id,dep.tax_rate, dep.tax_money,dep.tax_last_money,dep.other_field1,dep.other_field2,dep.other_field3, dep.other_field4,dep.other_field5,dep.material_type from jsh_depot_item dep,jsh_material mat where 1=1
and dep.header_id = #{depotheadId}
and dep.material_id=mat.id
and mat.enable_serial_number = #{enableSerialNumber}
and ifnull(dep.delete_flag,'0') !='1' and ifnull(mat.delete_flag,'0') !='1'

mapper.delete:
delete from jsh_depot_item
where 1=1
and header_id in
(

    #{depotheadId}

)

mapper.update:
update jsh_depot_item set delete_flag='1' where 1=1 and header_id in (
#{depotheadId}
)

update jsh_depot_item
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_depot_item where 1=1 and depot_id in (
#{depotId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_item where 1=1 and material_id in (
#{materialId}
) and ifnull(delete_flag,'0') !='1'

SELECT m.name MaterialName, m.model MaterialModel, mc.`Name` categoryName, CONCAT( '(', m.standard, ')', '(', m.color, ')' ) AS MaterialOther, m.unit MaterialUnit, ifnull(m.safety_stock,0) safetystock, IFNULL(intype.BasicInNumber ,0) BasicInNumber, IFNULL(outtype.BasicOutNumber ,0) BasicOutNumber, ( IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) ) BasicNumber, (IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) - ifnull(m.safety_stock,0)) BasicLinjieNumber FROM jsh_material m LEFT JOIN jsh_material_category mc ON mc.id = m.category_id LEFT JOIN ( SELECT di.material_id, ifnull(sum(basic_number), 0) AS BasicInNumber FROM jsh_depot_head dh INNER JOIN jsh_depot_item di ON dh.id = di.header_id AND ifnull(di.delete_flag, '0') != '1' WHERE dh.type = '入库'
and di.depot_id= ${pid}
AND ifnull(dh.delete_flag, '0') != '1' group by di.material_id ) intype ON intype.material_id = m.id LEFT JOIN ( SELECT di.material_id, ifnull(sum(basic_number), 0) AS BasicOutNumber FROM jsh_depot_head dh INNER JOIN jsh_depot_item di ON dh.id = di.header_id AND ifnull(di.delete_flag, '0') != '1' WHERE dh.type = '出库' AND dh.sub_type != '调拨'
and di.depot_id= ${pid}
AND ifnull(dh.delete_flag, '0') != '1' group by di.material_id ) outtype ON outtype.material_id = m.id WHERE 1 = 1 AND ifnull(m.delete_flag, '0') != '1' AND intype.BasicInNumber > 0 ORDER BY (IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) - ifnull(m.safety_stock,0))
limit #{offset},#{rows}
