mapper.select:
select * FROM jsh_depot_item where 1=1
and name like '%${name}%'
and type=${type}
and remark like '%${remark}%'
and ifnull(delete_flag,'0') !='1'
limit #{offset},#{rows}

select dep.id,dep.header_id,dep.material_id,dep.material_unit,dep.oper_number, dep.basic_number,dep.unit_price,dep.tax_unit_price,dep.all_price,dep.remark, dep.img,dep.incidentals,dep.depot_id,dep.another_depot_id,dep.tax_rate, dep.tax_money,dep.tax_last_money,dep.other_field1,dep.other_field2,dep.other_field3, dep.other_field4,dep.other_field5,dep.material_type from jsh_depot_item dep,jsh_material mat where 1=1
and dep.header_id = #{depotheadId}
and dep.material_id=mat.id
and mat.enable_serial_number = #{enableSerialNumber}
and ifnull(dep.delete_flag,'0') !='1' and ifnull(mat.delete_flag,'0') !='1'

mapper.delete:
delete from jsh_depot_item
where 1=1
and header_id in
(

    #{depotheadId}

)

mapper.update:
update jsh_depot_item set delete_flag='1' where 1=1 and header_id in (
#{depotheadId}
)

update jsh_depot_item
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_depot_item where 1=1 and depot_id in (
#{depotId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_item where 1=1 and material_id in (
#{materialId}
) and ifnull(delete_flag,'0') !='1'

select m.id MId, m.name MName, m.mfrs MMfrs, m.model MModel, m.standard MStandard,m.color MColor, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3, m.unit MaterialUnit, u.name unit_name,mcs.current_number,ifnull(m.safety_stock,0) safetystock, (ifnull(m.safety_stock,0)-mcs.current_number) linjieNumber from jsh_material m left join jsh_material_current_stock mcs on mcs.material_id=m.id and ifnull(mcs.delete_Flag,'0') !='1' left join jsh_depot_item di on di.material_id=m.id and ifnull(di.delete_Flag,'0') !='1' left join jsh_depot_head dh on di.header_id=dh.id and ifnull(dh.delete_flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where 1=1 and ifnull(m.delete_flag,'0') !='1' and ifnull(m.safety_stock,0) > mcs.current_number
and (m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and mcs.depot_id= ${pid}
group by m.id,m.name, m.model, m.unit, m.color, u.name order by linjieNumber desc
limit #{offset},#{rows}
